package day4_arithmeticOperator;

public class contribution {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//The program calculate the amount of the pay that will contributed to a retirement 
		//  plan if 5%, %8 or %10 monthly  pay is witheld.
		
		
		int monthlyPay=6000;
		double pay5;
		double pay8;
		double pay10;
		
		pay5=monthlyPay*0.05;
		pay8=monthlyPay*0.08;
		pay10=monthlyPay*0.1;
		System.out.println(" The contribution %5 retirement payment is: $"+ pay5);
		System.out.println(" The contribution %8 retirement payment is: $"+ pay8);
		System.out.println(" The contribution %10 retirement payment is: $"+ pay10);
		
		

			
			
		
		

	}

}
